import React, { useRef, useEffect, useState } from 'react'
import { AdminObj, DataRoomObjWithKey, RoomObj } from '../Types'
import Messenger from './Messenger'
import './Style/room.css';

type Props = {
    roomData: RoomObj
    error: string
    socketRef: React.MutableRefObject<any>
}

const Room: React.FC<Props> = ({ roomData, error, socketRef }) => {

    const [displayAddWord, setDisplayAddWord] = useState<boolean>(false)
    // DISPLAY //

    const [word, setWord] = useState<string>('')
    const [words, setWords] = useState<string []>([])
    const [users, setUsers] = useState<AdminObj[]>()

    const inputRef = React.useRef() as React.MutableRefObject<HTMLInputElement>;

    useEffect(() => {
        socketRef.current.emit("ask share rooms", true)

        socketRef.current.on("share rooms", (data: DataRoomObjWithKey) => {         
            console.log(data);

            if (roomData.id !== '') {
                Object.keys(data).map((id) => {                    
                    if (roomData.id === id) {
                        setUsers(data[id].users)            
                    }
                    return true
                })


            }
        })


    }, [roomData])


    const copyLink = (e: any) => {
        inputRef.current.select();

        document.execCommand('copy');

        e.target.focus();
    }

    const handleAddWord = (e: React.ChangeEvent<HTMLFormElement>) => {
        e.preventDefault()
        setWords([...words, word])
        setWord('')
    }

    return (
        <div className="room container">
            {error}
            {console.log(words)}
            <div className="d-flex flex-column justify-content-between align-items-between">
                <div>
                    <h2 className="mb-3">{roomData === undefined ? '' : roomData.name}</h2>
                    <div className="row">
                        <label>ID Room</label>
                        <div>
                            <input className="mx-auto copy-input" ref={inputRef} type="text" defaultValue={roomData === undefined ? '' : roomData.id}/>
                            <button className="mon-bouton mx-2 mb-3 rounded" onClick={(e) => copyLink(e)} style={{ filter: 'hue-rotate(18deg)'}}><i className="fas fa-clone"></i></button>
                        </div>
                        <div className="row">
                            { roomData.admin.id === socketRef.current.id
                                ? <div className="dropdown w-50">
                                    <button className="btn btn-warning dropdown-toggle text-light" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                                        Nombre de mots par personne
                                    </button>
                                    <div className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                                        <form className="dropdown-item row">
                                            <input className="w-75" defaultValue="10" type="number" min="10" max="1000"/>
                                            <button className="w-25" type="submit"><i className="fas fa-check"></i></button>
                                        </form>
                                    </div>
                                </div>

                                : ''
                            }
                            <div className="dropdown w-50">
                                <button onClick={() => setDisplayAddWord(!displayAddWord)} className="btn btn-warning text-light" type="button">
                                    Ajouter un mot
                                </button>
                                { displayAddWord 
                                    ? <form onSubmit={handleAddWord} className="dropdown-item row">
                                        <input value={word} onChange={(e) => setWord(e.target.value)} className="w-75" type="text"/>
                                        <button className="w-25" type="submit"><i className="fas fa-check"></i></button>
                                      </form>

                                    : ''
                                }
                              </div>
                        </div>
                    </div>
                </div>
                <div className="container">   
                    <div className="row mt-3 justify-content-between">
                        <div className="col-md-4 users">
                            <h3 className="mt-2">Joueurs</h3>
                            <ul>
                                { users !== undefined
                                    ? users.map((user, index) => {
                                        if (roomData.admin.id === user.id) {
                                            return <li key={index} className="grow" >
                                                        <p>{user.pseudo} <i className="fas fa-star"></i></p>
                                                   </li>
                                        } else {                                        
                                            return <li key={index} className="grow" >
                                                        <p>{user.pseudo}</p>
                                                   </li>
                                        }
                                    })

                                    : ''
                                }
                            </ul>
                        </div>
                        <Messenger idRoom={roomData.id} socketRef={socketRef} />
                    </div>
                </div>

            </div>
        </div>
    )
}

export default Room
