import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client'
import './App.css';
import CreateJoinRoom from './Components/CreateJoinRoom';

function App() {
  const [newPseudo, setNewPseudo] = useState('')
  const [ancientPseudo, setAnciantPseudo] = useState<string | null>()

  const socketRef: React.MutableRefObject<any> = useRef()

  useEffect(() => {
    socketRef.current = io.connect('/')

    socketRef.current.on("your id", (id: string | number) => {
        console.log(id)
    })
    setAnciantPseudo(localStorage.getItem('myPseudo'))
  }, [])

  const handleOnSubmit = () => {
    localStorage.setItem('myPseudo', newPseudo)
  }

  return (
    <div className="App">
      <h1>Time's Up</h1>
      <form onSubmit={handleOnSubmit} className="mt-5">
        <input placeholder={ancientPseudo === null ? 'PseudoStylayDu91' : ancientPseudo} type="text" onChange={(e) => setNewPseudo(e.target.value)}/>
        <br/>
        <button className="mt-2 btn btn-primary" type="submit">Valider</button>
      </form>
      <CreateJoinRoom />
    </div>
  );
}
