import React, {useState, useEffect} from 'react'

type Props = {
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
}

const Game1: React.FC<Props>  = ({ idRoom, socketRef }) => {

    const [words, setWords] = useState<string []>([])
    const [actualWord, setActualWord] = useState<string>()

    const getRandomInteger = (min: number, max: number) => {
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

    useEffect(() => {
        socketRef.current.emit("ask words", idRoom);
        socketRef.current.on("words", (words: string []) => {

            let randomNumber = getRandomInteger(0, words.length - 1)

            words.forEach((word, index) => {
                if (randomNumber === index) {
                    console.log(word);   
                    setActualWord(word)
                    words.splice(index, 1)       
                }
            })
            setWords(words);
        });
    }, [])

    const makeRandomWord = () => {
        let randomNumber = getRandomInteger(0, words.length - 1)

        words.forEach((word, index) => {
            if (randomNumber === index) {
                console.log(word);
                setActualWord(word)
                words.splice(index, 1)                    
            }
        })
    }

    return (
        <div className="row justify-content-center">
            {console.log(words)}
            <button className="btn btn-primary">
            <i className="fas fa-times"></i>
            </button>
            <p className="text-light">{actualWord}</p>
            <button className="btn btn-primary">
                <i className="fas fa-check"></i>
            </button>
        </div>
    )
}

export default Game1
