import React, { useEffect, useState } from 'react'
import { RulesObj } from '../Types'

type Props = {
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
}

const NextRound: React.FC<Props> = ({ idRoom, socketRef }) => {

    const [round, setRound] = useState<number>(0)
    const [displayGame2, setDisplayGame2] = useState(false)

    useEffect(() => {
        socketRef.current.emit("ask rules", idRoom)
        socketRef.current.on("rules", (rulesData: RulesObj) => {
            setRound(rulesData.round)
        })
    }, [])

    const startNextRound = () => {
        setDisplayGame2(true)
    }

    return (
        <div>

            <p>Phase {round} fini !</p>
            <button onClick={() => startNextRound()} className="btn btn-primary">
                Phase suivante !
            </button>
        </div>
    )
}

export default NextRound
