                            return <div className="row justify-content-end">
                                    <li className="your-message text-end my-2 mr-4 px-3" key={index}>
                                        <p className="mt-2">{messageBody.message}</p>                            
                                    </li>
                                </div> 
                            } else {
                            return <div className="row justify-content-start">
                                    <li className="others-message text-start my-2 mr-4 px-3" key={index}>
                                        <p className="mt-2">{messageBody.message} {messageBody.myPseudo}</p>                            
                                    </li>
                                </div> 
                            }

                })}
                    </>

                    : ''
                }
            </ul> */}
            <form onSubmit={sendMessage} className="message-form row justify-content-between">
                <textarea placeholder="Dit un truc..." value={message} onChange={(e) => setMessage(e.target.value)}></textarea>
                <button className="mon-bouton rounded-circle" type="submit"><i className="fas fa-paper-plane"></i></button>
            </form>
        </div>
    )
}

export default Messenger
