import React, { useEffect, useRef, useState } from 'react'
import io from 'socket.io-client'
import { MessageObject } from '../Types'

type Props = {
    idRoom: string | number
}

const Messenger: React.FC<Props> = ({ idRoom }) => {

    const [myPseudo, setMyPseudo] = useState<string | null>('')
    const [myId, setMyId] = useState<string | number>()
    const [message, setMessage] = useState<string>('')
    const [messages, setMessages] = useState<MessageObject []>([])

    const socketRef: React.MutableRefObject<any> = useRef()

    useEffect(() => {
        socketRef.current = io.connect('/')   

        socketRef.current.on("your id", (id: string | number) => {
            setMyId(id)
        })        

        setMyPseudo(localStorage.getItem('myPseudo'))

    }, [])

    const sendMessage = (e: React.ChangeEvent<HTMLFormElement>) => {
        e.preventDefault()
        if (message !== '') {
            const messageObject = {
                body: { 
                    message,
                    myPseudo,
                    myId
                }, 
                idRoom
            }

            setMessage('')
            socketRef.current.emit("send message", messageObject)
        }  
    }

    return (
        <div>
            <ul>
                { messages !== undefined
                    ? <>
                        { messages.map((messageObj) => {
                            return <li>{messageObj.body.message}</li>
                        })}
                    </>

                    : ''
                }
            </ul>
            <form onSubmit={sendMessage}>
            <textarea placeholder="Dit un truc..." value={message} onChange={(e) => setMessage(e.target.value)}></textarea>
                <button type="submit">Envover</button>
            </form>
        </div>
    )
}

export default Messenger
