import React, { useRef, useEffect, useState } from 'react'
import io from 'socket.io-client'
import { AdminObj, RoomObj } from '../Types'

type Props = {
    roomData: RoomObj
}

const Room: React.FC<Props> = ({ roomData }) => {

    const [users, setUsers] = useState<AdminObj []>()

    const socketRef: React.MutableRefObject<any> = useRef()

    let usersD = []

    useEffect(() => {
        socketRef.current = io.connect('/')        

        socketRef.current.on("share users", (usersData: AdminObj []) => {                     
            usersD.push(usersData)
        })
    }, [])

    const inputRef = React.useRef() as React.MutableRefObject<HTMLInputElement>;

    const copyLink = (e: any) => {
        inputRef.current.select();

        document.execCommand('copy');

        e.target.focus();
    }

    return (
        <div>
            Room: {roomData === undefined ? '' : roomData.name}
            <br/>
            Id:
            <input className="mon-input" ref={inputRef} type="text" defaultValue={roomData === undefined ? '' : roomData.id}/>
            <button className="bouton mx-2" onClick={(e) => copyLink(e)} style={{ marginLeft: '1rem'}}>Copier</button>
            <br/>
            users
            {console.log(users)}
            {/* {
                users?.map((user) => {
                    return <p>{user}</p>
                })
            } */}
            { users !== undefined
                ? users.map((user) => {
                    return <p>{user}</p>
                })

                : ''
            }
        </div>
    )
}

export default Room
