import React, { useEffect, useRef, useState } from 'react'
import { RulesObj } from '../Types'

type Props = {
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
}

const Chrono: React.FC<Props>  = ({ idRoom, socketRef }) => {

    const [time, setTime] = useState<number>(0)

    const chronoRef: React.MutableRefObject<number | undefined> = useRef()

    useEffect(() => {        
        socketRef.current.emit("ask rules", idRoom)

        socketRef.current.on("rules", (rulesData: RulesObj) => {
            console.log(rulesData)
            setTime(rulesData.time)
            chronoRef.current = rulesData.time
        })

        let interval = setInterval(() => {
            setTime(time => time - 1)
        }, 1000)
    }, [])

    // const chronometer = () => {
