import React, { useRef, useEffect, useState } from 'react'
import { AdminObj, DataRoomObjWithKey, RoomObj } from '../Types'
import Messenger from './Messenger'
import './Style/room.css';

type Props = {
    roomData: RoomObj
    error: string
    socketRef: React.MutableRefObject<any>
}

const Room: React.FC<Props> = ({ roomData, error, socketRef }) => {

    // const [dataRoom, setDataRoom] = useState<DataRoomObjWithKey>()
    const [users, setUsers] = useState<AdminObj[]>()

    const inputRef = React.useRef() as React.MutableRefObject<HTMLInputElement>;

    useEffect(() => {
        socketRef.current.emit("ask share rooms", true)

        socketRef.current.on("share rooms", (usersData: DataRoomObjWithKey) => {            
            if (roomData.id !== '') {
                Object.keys(usersData).map((id) => {                    
                    if (roomData.id === id) {
                        setUsers(usersData[id].users)            
                    }
                    return true
                })
            }
        })
    }, [roomData])


    const copyLink = (e: any) => {
        inputRef.current.select();

        document.execCommand('copy');

        e.target.focus();
    }

    return (
        <div className="room container">
            <h2 className="mb-3">{roomData === undefined ? '' : roomData.name}</h2>
            <div className="row">
                <label>ID Room</label>
                <div>
                    <input className="mx-auto" ref={inputRef} type="text" defaultValue={roomData === undefined ? '' : roomData.id}/>
                    <button className="mon-bouton mx-2 mb-3" onClick={(e) => copyLink(e)} style={{ filter: 'hue-rotate(18deg)'}}>Copier</button>
                </div>
            </div>
        {error}
            <div className="row">
                <div className="col-md-4 users">
                    <h3>Joueurs</h3>
                    { users !== undefined
                        ? users.map((user, index) => {
                            return <p key={index} >{user.pseudo}</p>
                        })

                        : ''
                    }
                </div>
                <div className="col-md-8 messenger">
                    <Messenger idRoom={roomData.id} socketRef={socketRef} />
                </div>
            </div>
        </div>
    )
}

export default Room
