import React, { useEffect, useRef, useState } from 'react'
import { AdminObj, TeamObj } from '../Types';
import Game1 from './Game1';
import './Style/startGame.css';

type Props = {
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
}

type TeamPlayers = {
    id: string | number | undefined
    pseudo: string | null | undefined
}

const StartGame: React.FC<Props> = ({ idRoom, socketRef }) => {

    const [startGamePreview, setStartGamePreview] = useState<boolean>(false)
    const yourPlayingRef: React.MutableRefObject<boolean | undefined> = useRef()
    // DISPLAY


    const [actualTeam, setActualTeam] = useState<string>()
    const [actualPlayer, setActualPlayer] = useState<string | null | undefined>()
    const [actualPlayerId, setActualPlayerId] = useState<string | number | undefined>()

    const [teams, setTeams] = useState<TeamObj[]>()
    const [teamPlayers, setTeamPlayers] = useState<TeamPlayers []>([])

    const [users, setUsers] = useState<AdminObj[]>()



    useEffect(() => {
        socketRef.current.emit("ask teams", idRoom);
        socketRef.current.on("teams", (teams: TeamObj []) => {
            console.log(teams);
            setTeams(teams)
            setActualTeam(teams[0].name)
            setActualPlayer(teams[0].players[0].pseudo)
            setActualPlayerId(teams[0].players[0].id)
            console.log(teams[0].players[0].id);
        });


        socketRef.current.emit("ask users", idRoom)

        socketRef.current.on("users", (usersData: AdminObj []) => {                      
            setUsers(usersData);
        })
        console.log(socketRef.current.id);
    }, [])

    const startGame = () => {



        setStartGamePreview(true)
    }

    return (
        <div className="row justify-content-center align-items-center" style={{height: '70vh'}}>
            { startGamePreview
                ? <Game1 socketRef={socketRef} idRoom={idRoom}/>

                : <>
                    { 
                        users?.forEach((user) => {
                            if (user.id === socketRef.current.id && user.playing === true) {
                                yourPlayingRef.current = true
                            }                            
                        })
                    }

                    { yourPlayingRef.current
                        ? <div>
                                <p className="text-light">C'est à toi de jouer !</p>
                                <button onClick={() => startGame()} className="start-bouton">Let's Go</button>
                          </div> 

                        : <div className="text-light">
                            <p>{actualPlayer} de la team {actualTeam} s'apprête à jouer</p>
                          </div>                        
                    }     
                </>      
            }            
        </div>
    )
}

export default StartGame
