import React, { useEffect, useState } from 'react';
import CreateJoinRoom from './Components/CreateJoinRoom';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import { Beforeunload } from 'react-beforeunload';

import './App.css';

const App = () => {

  const [displayCreateJoinRoom, setDisplayCreateJoinRoom] = useState(false)

  const [newPseudo, setNewPseudo] = useState('')
  const [ancientPseudo, setAnciantPseudo] = useState<string | null>()

  useEffect(() => {
    setAnciantPseudo(localStorage.getItem('myPseudo'))
  }, [])

  const handleOnSubmit = (e: React.ChangeEvent<HTMLFormElement>) => {
    e.preventDefault()
    if (newPseudo !== '') {
      localStorage.setItem('myPseudo', newPseudo)
    }

    setDisplayCreateJoinRoom(true)
  }

  const handleRefresh = (e: any) => {
    e.preventDefault()
    console.log('zerfzergze');
    window.alert('erfer')
  }

  return (
    <div className="App">
        <Beforeunload onBeforeunload={(e) => handleRefresh(e)} />
        <h1 className="mt-3">Jeu avec du temps <i className="fas fa-stopwatch"></i></h1>
        <main>
          <div className="d-flex flex-column justify-content-between">
            { displayCreateJoinRoom 
                ? <CreateJoinRoom />

                : <>
                    <form onSubmit={handleOnSubmit} className="mt-3">
                      <input className="mon-input" placeholder={ancientPseudo === null ? 'PseudoStylayDu91Rpz' : ancientPseudo} type="text" onChange={(e) => setNewPseudo(e.target.value)}/>
                      <br/>
                      <button className="mon-bouton mt-3" type="submit">GO <i className="fas fa-arrow-alt-circle-right"></i></button>
                    </form>
                    <div className="row justify-content-center mt-5 mx-1">
                      <div className="gradient-border col-md-12" id="box">
                      <Carousel 
                        autoPlay={true} 
                        infiniteLoop={true} 
                        dynamicHeight={true}
                        showThumbs={false}
                      >
                        <div className="item" >
                            <h2 className="mb-3">Tour 1</h2>
                            <p>Chacun sont tour il va falloir faire deviner le plus de mots à vos coéquipiers !</p>
                            <p>Vous pouvez, pour cela, utiliser autant de mots que vous le souhaitez</p>
                        </div>
                        <div className="item">
                          <h2 className="mb-2">Tour 2</h2>
                          <p>Comme le tour précédent il va falloir faire deviner le plus de mots à vos coéquipiers !</p>
                          <p>En revanche, vous ne pourrez utiliser qu'un seul mot</p>
                        </div>
                        <div className="item">
                          <h2 className="mb-2">Tour 3</h2>
                          <p>Pour le dernier tour il va falloir faire deviner en dessinant</p>
                          <p>Interdiction de parler !</p>
                        </div>
                    </Carousel>
                      </div>
                    </div>
                  </>
            }
          </div>
        </main>
      </div>
  );
}

export default App
