import React, { useEffect, useState } from 'react'
import { AdminObj, RoomObj, TeamObj } from '../Types'
import Game1 from './Game1'

type Props = {
    playerName: string | null | undefined
    team: string | undefined
    points: number
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
}

const NextTurn: React.FC<Props> = ({ playerName, team, points, idRoom, socketRef }) => {

    const [startGamePreview, setStartGamePreview] = useState(false)

    useEffect(() => {
        socketRef.current.emit("ask rooms", idRoom);

        socketRef.current.on("rooms", (rooms: RoomObj []) => {
            console.log(rooms);

        });

        socketRef.current.emit("ask teams", idRoom);

        socketRef.current.on("teams", (teams: TeamObj []) => {
            console.log(teams);

            teams.forEach((team) => {
                if (team.playing === true) {                    
                    console.log(team.name);
                }
            })
        });
    }, [])

    const startGame = () => {
        socketRef.current.emit("start game", idRoom)
        setStartGamePreview(true)
    }

    return (
        <div className="text-light">
            { startGamePreview
                ? <Game1 socketRef={socketRef} idRoom={idRoom} />

                : <>
                    {playerName} de la team {team} a remport√© {points} {points <= 1 ? 'point' : 'points'}
                    <br></br>
                    <button onClick={() => startGame()} className="mon-bouton">
                        Next
                    </button>  
                </>      
            } 

        </div>
    )
}

export default NextTurn
