import React, { useEffect, useState } from 'react';
import CreateJoinRoom from './Components/CreateJoinRoom';
import './App.css';

const App = () => {

  const [displayCreateJoinRoom, setDisplayCreateJoinRoom] = useState(false)

  const [newPseudo, setNewPseudo] = useState('')
  const [ancientPseudo, setAnciantPseudo] = useState<string | null>()

  useEffect(() => {
    setAnciantPseudo(localStorage.getItem('myPseudo'))
  }, [])

  const handleOnSubmit = (e: React.ChangeEvent<HTMLFormElement>) => {
    e.preventDefault()
    setDisplayCreateJoinRoom(true)
    if (newPseudo !== '') {
      localStorage.setItem('myPseudo', newPseudo)
    }
  }

  return (
    <div className="App">
      <h1 className="mt-3">Jeu Avec Du Temps</h1>
      <main className="d-flex flex-column justify-content-between">
        { displayCreateJoinRoom 
            ? <CreateJoinRoom />

            : <>
                <form onSubmit={handleOnSubmit} className="mt-5">
                  <input className="mon-input" placeholder={ancientPseudo === null ? 'PseudoStylayDu91' : ancientPseudo} type="text" onChange={(e) => setNewPseudo(e.target.value)}/>
                  <br/>
                  <button className="mon-bouton mt-3" type="submit">GO <span>&#9658;</span></button>
                </form>
                <div className="row justify-content-between mt-5">
                  <div className="gradient-border col-md-4" id="box"></div>
                  <div className="gradient-border col-md-4" id="box"></div>
                  <div className="gradient-border col-md-4" id="box"></div>
                </div>
              </>
        }

      </main>
    </div>
  );
}

export default App
