import React, { useEffect, useState } from 'react'
import Game from './Game';
import './Style/startGame.css';

type Props = {
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
}

const StartGame: React.FC<Props> = ({ idRoom, socketRef }) => {

    const [setStartGame, setSetStartGame] = useState<boolean>(false)
    // DISPLAY

    const [words, setWords] = useState<string []>([])

    useEffect(() => {
        socketRef.current.emit("ask words", idRoom);
        socketRef.current.on("words", (words: string []) => {
            setWords(words);
        });

    }, [])

    return (
        <div className="row justify-content-center align-items-center" style={{height: '70vh'}}>
            { setStartGame 
                ? <Game />

                : <button onClick={() => setSetStartGame(true)} className="start-bouton">Let's Go</button>

            }

        </div>
    )
}

export default StartGame
