import React, { useRef, useEffect, useState } from 'react'
import io from 'socket.io-client'
import { AdminObj, DataRoomObjWithKey, RoomObj } from '../Types'

type Props = {
    roomData: RoomObj
}

const Room: React.FC<Props> = ({ roomData }) => {

    const [dataRoom, setDataRoom] = useState<DataRoomObjWithKey>()
    const [users, setUsers] = useState<AdminObj>()

    const socketRef: React.MutableRefObject<any> = useRef()

    useEffect(() => {
        socketRef.current = io.connect('/')

        socketRef.current.on("share rooms", (usersData: DataRoomObjWithKey) => {
            setDataRoom(usersData);

            Object.keys(usersData).map((id) => {
                if (id === roomData.id) {
                    setUsers(usersData[id].users)                     
                }
            })

        })
    }, [])

    const inputRef = React.useRef() as React.MutableRefObject<HTMLInputElement>;

    const copyLink = (e: any) => {
        inputRef.current.select();

        document.execCommand('copy');

        e.target.focus();
    }

    return (
        <div>
            Room: {roomData === undefined ? '' : roomData.name}
            <br/>
            Id:
            <input className="mon-input" ref={inputRef} type="text" defaultValue={roomData === undefined ? '' : roomData.id}/>
            <button className="bouton mx-2" onClick={(e) => copyLink(e)} style={{ marginLeft: '1rem'}}>Copier</button>
            <br/>
            users
            {console.log(dataRoom, 'dataRoom')}
            {console.log(users, 'users')}
            { dataRoom !== undefined
                ? <div>
                    {
                        Object.keys(dataRoom).map((id) => {
                            if (id === roomData.id) {
                               return console.table(dataRoom[id].users)                            
                            }
                        })
                    }
                </div>

                : ''
            }

        </div>
    )
}

export default Room
