import React, { useEffect, useRef, useState } from 'react'
import { TeamObj } from '../Types';

type Props = {
    idRoom: string | number
    socketRef: React.MutableRefObject<any>
    imPlaying: boolean | undefined
}

const Board: React.FC<Props>  = ({ idRoom, socketRef, imPlaying }) => {  

    useEffect(() => {
        socketRef.current.emit("ask teams", idRoom);
        socketRef.current.on("teams", (teams: TeamObj []) => {            
            teams.forEach((team) => {
                if (team.playing === true) {                    
                    team.players.forEach((player) => {
                        if (player.playing === true) {
                            if (player.id === socketRef.current.id) {    

                            }
                        }
                    })
                }
            })
        }); 

        socketRef.current.on("board erased", (bool: boolean) => {
            let canvas = document.getElementById('board') as HTMLCanvasElement;        

            let ctx = canvas.getContext('2d') as CanvasRenderingContext2D;
            let sketch = document.querySelector('#sketch') as HTMLDivElement;
            let sketch_style = getComputedStyle(sketch);
            canvas.width = parseInt(sketch_style.getPropertyValue('width'));
            canvas.height = parseInt(sketch_style.getPropertyValue('height'));

            ctx.clearRect(0, 0, 0, 0)

            canvas.height = 400
        })

        if (imPlaying === true) {
            drawOnCanvas();
        } else {
            socketRef.current.on("canvas data drawn img", (dataDrawing: any) => {                                                    
                console.log(dataDrawing);

                let canvas = document.getElementById('board') as HTMLCanvasElement;        

                let ctx = canvas.getContext('2d') as CanvasRenderingContext2D;
                ctx.beginPath();
                ctx.moveTo(dataDrawing.last_mouse.x, dataDrawing.last_mouse.y);
                ctx.lineTo(dataDrawing.mouse.x, dataDrawing.mouse.y);
                ctx.closePath();
                ctx.stroke();

                // let image = new Image();
                // let canvas = document.getElementById('board') as HTMLCanvasElement;        

                // let ctx = canvas.getContext('2d') as CanvasRenderingContext2D;

                // image.onload = () => {
                //     ctx.drawImage(image, 0, 0);
                // }
                // image.src = dataDrawing
            })
        }

    }, [imPlaying])

    const drawOnCanvas = () => {
        let canvas = document.getElementById('board') as HTMLCanvasElement;        

        let ctx = canvas.getContext('2d') as CanvasRenderingContext2D;

        let sketch = document.querySelector('#sketch') as HTMLDivElement;
        let sketch_style = getComputedStyle(sketch);
        canvas.width = parseInt(sketch_style.getPropertyValue('width'));
        canvas.height = parseInt(sketch_style.getPropertyValue('height'));

        let mouse = {x: 0, y: 0};
        let last_mouse = {x: 0, y: 0};

        /* Mouse Capturing Work */
        canvas.addEventListener('mousemove', function(e) {
            console.log();

            last_mouse.x = mouse.x;
            last_mouse.y = mouse.y;

            mouse.x = e.pageX - this.offsetLeft;
            mouse.y = e.pageY - this.offsetTop;
        }, false);


        /* Drawing on Paint App */
        ctx.lineWidth = 5;
        ctx.lineJoin = 'round';
        ctx.lineCap = 'round';
        ctx.strokeStyle = 'black';

        canvas.addEventListener('mousedown', function(e) {
            canvas.addEventListener('mousemove', onPaint, false);
        }, false);

        canvas.addEventListener('mouseup', function() {
            canvas.removeEventListener('mousemove', onPaint, false);
        }, false);

        let onPaint = function() {
            ctx.beginPath();
            ctx.moveTo(last_mouse.x, last_mouse.y);
            ctx.lineTo(mouse.x, mouse.y);
            ctx.closePath();
            ctx.stroke();

            let drawingObj = {
                idRoom,
                last_mouse,
                mouse
            }

            socketRef.current.emit("canvas data drawn", drawingObj);
        };
    }      

    return (
        <div>
            <div className="sketch" id="sketch">
                <input type="range"/>
                <canvas id="board" width="1080" height="400"></canvas>    
            </div>
        </div>
    )
}

export default Board
