import React, { useEffect, useRef } from 'react'

const Board = () => {

    const timeout: React.MutableRefObject< any > = useRef()

    useEffect(() => {

        drawOnCanvas();

    }, [])

    const drawOnCanvas = () => {
        let canvas = document.getElementById('board') as HTMLCanvasElement;        

        let ctx = canvas.getContext('2d') as CanvasRenderingContext2D;

        let sketch = document.querySelector('#sketch') as HTMLDivElement;
        let sketch_style = getComputedStyle(sketch);
        canvas.width = parseInt(sketch_style.getPropertyValue('width'));
        canvas.height = parseInt(sketch_style.getPropertyValue('height'));

        let mouse = {x: 0, y: 0};
        let last_mouse = {x: 0, y: 0};

        /* Mouse Capturing Work */
        canvas.addEventListener('mousemove', function(e) {
            console.log();

            last_mouse.x = mouse.x;
            last_mouse.y = mouse.y;

            mouse.x = e.pageX - this.offsetLeft;
            mouse.y = e.pageY - this.offsetTop;
        }, false);


        /* Drawing on Paint App */
        ctx.lineWidth = 5;
        ctx.lineJoin = 'round';
        ctx.lineCap = 'round';
        ctx.strokeStyle = 'blue';

        canvas.addEventListener('mousedown', function(e) {
            canvas.addEventListener('mousemove', onPaint, false);
        }, false);

        canvas.addEventListener('mouseup', function() {
            canvas.removeEventListener('mousemove', onPaint, false);
        }, false);

        let root: any = this;
        let base64ImageData;

        let onPaint = function() {
            ctx.beginPath();
            ctx.moveTo(last_mouse.x, last_mouse.y);
            ctx.lineTo(mouse.x, mouse.y);
            ctx.closePath();
            ctx.stroke();

            if (root.timeout != undefined) {
                clearTimeout(root.timeout)
            }

            root.timeout = setTimeout(() => {
                base64ImageData = canvas.toDataURL('image/png')
            }, 1000)
        };

        console.log(base64ImageData);

    }      

    return (
        <div>
            <div className="sketch" id="sketch">
                <canvas id="board" width="700" height="420"></canvas>    
            </div>
        </div>
    )
}

export default Board
